const url = "http://localhost/cacc_backend/api/";
const config = {
    headers: {
        'Content-Type': 'application/json',
        
    }
};

function getValidToken() {
    const validToken = localStorage.getItem('validToken');
    if(validToken){
        this.config.headers.Authorization = "Bearer " + validToken;
    }
}

export {url, config};
