import { Route, Routes } from 'react-router-dom';
import Contato from '../../pages/Contato/contato';
import Eventos from '../../pages/Eventos/Eventos';
import Inicio from '../../pages/Publicacoes/Publicacoes';
import Login from '../../pages/Login/Login';
import QuemSomos from '../../pages/QuemSomos/quemSomos';
import "./Body.css";
import { useState } from 'react';


const Body = () => {
    const [scrollPageBody, setScrollPageBody] = useState(false);
    window.onscroll = () => { 
        let offSetScreen = window.pageYOffset;
        console.log(offSetScreen);
        if(offSetScreen > 100)
            setScrollPageBody(true);    
        else
            setScrollPageBody(false);
    };

    return (
        <div className={!scrollPageBody ? 'Body' : 'Body BodyScrolled'}>
            <div className='ContentBox'>
                <div className='Page'>
                    <Routes>
                        <Route path="/inicio" element={<Inicio/>}/>
                        <Route path="/eventos" element={<Eventos/>}/>
                        <Route path="/contato" element={<Contato/>}/>
                        <Route path="/quem_somos" element={<QuemSomos/>}/>
                    </Routes>
                </div>
            </div>
        </div>
    )
}

export default Body;
