import "./DiretoriaAdmin.css";
import { useEffect, useState } from "react";
import { getFullDiretoria, alternarAtividade } from "../../../hooks/useApi";
import { FaImage, FaRegEdit, FaHandPointUp, FaHandPointDown } from "react-icons/fa";

const DiretoriaAdmin = () => {

    const [list, setList] = useState([]);

    useEffect(() => {
        refreshTable();
    }, []);

    const visualizarImage = (id) => {

    }

    const editarRegistro = (item) => {

    }

    const alternarAtividadeButton = (id) => {
        console.log(id);
        alternarAtividade(id)
            .then((response) => {
                setList(list);
            })
            .catch((error) => alert(error));
    }

    return (
        <div className="Diretoria">
            <div className="DiretoriaBox">
                <div className="DiretoriaBoxHeader">
                    <h2>Gerenciamento da Diretoria</h2>
                </div>
                <div className="DiretoriaBoxBody">
                    <div className="RowFilters">
                        {/* TODO: FILTERS AND INSERT BUTTON */}
                    </div>
                    <div className="RowTable">
                        <table className="DiretoriaTable">
                            <thead>
                                <tr>
                                    <th></th>
                                    <th></th>
                                    <th></th>
                                    <th>Id</th>
                                    <th>Nome</th>
                                    <th>Cargo</th>
                                    <th>Atuando</th>
                                </tr>
                            </thead>
                            <tbody className="DiretoriaTableBody">
                                {
                                    list.map((item, i) => {
                                        return (
                                            <tr>
                                                <td><button onClick={() => { visualizarImage(item.iddiretoria) }} title="Visualizar Imagem" className="Button AquaButton"><FaImage/></button></td>
                                                <td><button onClick={() => { editarRegistro(item)}} title="Editar Registro" className="Button BlueButton"><FaRegEdit/></button></td>
                                                <td><button onClick={() => { alternarAtividadeButton(item.iddiretoria)}} title={item.ativo == "S" ? "Desativar" : "Ativar"} className={item.ativo == "S" ? "Button RedButton" : "Button GreenButton"}>{item.ativo == "S" ? <FaHandPointDown/> : <FaHandPointUp/>}</button></td>
                                                <td>{item.iddiretoria}</td>
                                                <td>{item.nome}</td>
                                                <td>{item.cargo}</td>
                                                <td>{item.ativo == "S" ? "Sim" : "Não"}</td>    
                                            </tr>
                                        )
                                    })
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default DiretoriaAdmin;
