import "./Modal.css";
import { FaTimes } from "react-icons/fa";


const Modal = (children) => {

    const modalBakcground = document.getElementsByClassName("ModalBackground");
    // modalBakcground.onclick = () => {
    //     setOpenModal(false);
    // }

    const handleCloseModal = () => {
        setOpenModal(false);
    }

    const title = "Teste";
    const btnText = "Teste";

    return (
        <div className="ModalBackground">
            <div className="Modal">
                <div className="ModalBox">
                    <div className="ModalBoxHeader">
                        <h2>{title}</h2>
                        <FaTimes onClick={handleCloseModal}/>
                    </div>
                    <div className="ModalBoxBody">
                        {children}
                    </div>
                    <div className="ModalBoxFooter">
                        <button>{btnText}</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Modal;
